import React, { useEffect, useState } from "react";
import { Pie } from "react-chartjs-2"; 
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js'; 
import './RevenueCheck.css'

ChartJS.register(ArcElement, Tooltip, Legend);

const RevenueCheck = () => {
    const [shopRevenue, setShopRevenue] = useState([]);
    const [totalRevenue, setTotalRevenue] = useState(0);
    const [selectedShop, setSelectedShop] = useState(null);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const checkRevenue = async () => {
            try {
                setIsLoading(true);
                const response = await fetch('http://localhost:5000/admin/checkrevenue', {
                    method: 'GET',
                    credentials: 'include'
                });

                if (!response.ok) {
                    throw new Error('Failed to fetch revenue data');
                }

                const data = await response.json();
                setTotalRevenue(data.totalRevenue);
                setShopRevenue(data.shopRevenues);
                setError(null);
            } catch (error) {
                console.error("Error fetching revenue data:", error);
                setError(error.message);
                setShopRevenue([]);
                setTotalRevenue(0);
            } finally {
                setIsLoading(false);
            }
        };

        checkRevenue();
    }, []);

    const handleChartElementClick = (event, elements) => {
        if (elements.length > 0) {
            const clickedIndex = elements[0].index;
            const selectedShopData = shopRevenue[clickedIndex];
            setSelectedShop(selectedShopData);
        }
    };

    const chartData = {
        labels: shopRevenue.map((shop) => shop.shopName),
        datasets: [{
            label: 'Shop Revenue',
            data: shopRevenue.map((shop) => shop.platformFee),
            backgroundColor: [
                '#FF6384', '#36A2EB', '#FFCE56', 
                '#4BC0C0', '#9966FF', '#FF9F40',
                '#FF5733', '#33FF57', '#3357FF'
            ],
            hoverBackgroundColor: [
                '#FF3366', '#2196F3', '#FFC107', 
                '#20BDBE', '#6633FF', '#FF6F00',
                '#FF2E00', '#2EFF00', '#0040FF'
            ]
        }]
    };

    const chartOptions = {
        responsive: true,
        onClick: handleChartElementClick,
        plugins: {
            legend: {
                position: 'right',
                labels: {
                    boxWidth: 20,
                }
            }
        }
    };

    if (isLoading) {
        return <div className="rc-loading">Loading revenue data...</div>;
    }

    if (error) {
        return <div className="rc-error">Error: {error}</div>;
    }

    return (
        <div className="rc-revenue-check">
            <div className="rc-revenue-summary">
                <h1>Total Revenue: ₹{totalRevenue.toLocaleString()}</h1>
                
                <div className="rc-revenue-content">
                    <div className="rc-chart-section">
                        <h2>Revenue Distribution</h2>
                        <div className="rc-pie-chart">
                            <Pie 
                                data={chartData} 
                                options={chartOptions} 
                            />
                        </div>
                    </div>

                    {selectedShop && (
                        <div className="rc-selected-shop-details">
                            <h2>{selectedShop.shopName} Revenue Breakdown</h2>
                            <div className="rc-shop-summary">
                                <div className="rc-shop-summary-item">
                                    <span>Total Platform Fee:</span>
                                    <strong>₹{selectedShop.platformFee.toLocaleString()}</strong>
                                </div>
                            </div>

                            {selectedShop.groundRevenues && selectedShop.groundRevenues.length > 0 ? (
                                <div className="rc-ground-revenues">
                                    <h3>Ground-wise Revenue</h3>
                                    <table>
                                        <thead>
                                            <tr>
                                                <th>Ground Name</th>
                                                <th>Platform Fee</th>
                                                <th>Ground Fee</th>
                                                <th>Total Revenue</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {selectedShop.groundRevenues.map((ground) => (
                                                <tr key={ground.groundId || ground.groundName}>
                                                    <td>{ground.groundName}</td>
                                                    <td>₹{ground.platformFee.toLocaleString()}</td>
                                                    <td>₹{ground.groundFee.toLocaleString()}</td>
                                                    <td>₹{(ground.platformFee + ground.groundFee).toLocaleString()}</td>
                                                </tr>
                                            ))}
                                        </tbody>
                                        <tfoot>
                                            <tr>
                                                <td colSpan="3"><strong>Total Ground Revenue</strong></td>
                                                <td>
                                                    <strong>
                                                        ₹{selectedShop.groundRevenues.reduce(
                                                            (total, ground) => total + ground.platformFee + ground.groundFee, 
                                                            0
                                                        ).toLocaleString()}
                                                    </strong>
                                                </td>
                                            </tr>
                                        </tfoot>
                                    </table>
                                </div>
                            ) : (
                                <p>No ground revenue details available.</p>
                            )}
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default RevenueCheck;
