import React, { useEffect, useState, useRef } from "react";
import NewsItem1 from "./NewsItem1";
import './News.css';

const News1 = () => {
    const [articles, setArticles] = useState([]);
    const scrollContainerRef = useRef(null);

    useEffect(() => {
        fetch("https://newsdata.io/api/1/news?apikey=pub_66465d644c11d4df42868252ecc7a8089cd1d&q=cricket")
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then((data) => setArticles(data.results || []))
            .catch((error) => console.error("Error fetching articles:", error));
    }, []);

    const scrollLeft = () => {
        if (scrollContainerRef.current) {
            scrollContainerRef.current.scrollBy({ left: -300, behavior: 'smooth' });
        }
    };

    const scrollRight = () => {
        if (scrollContainerRef.current) {
            scrollContainerRef.current.scrollBy({ left: 300, behavior: 'smooth' });
        }
    };

    return (
        <div className="news-wrapper">
            <h2 className="news-title">Trending News</h2>
            <button className="scroll-button left" onClick={scrollLeft}>
                <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path d="M15 6L9 12L15 18" stroke="white" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                </svg>
            </button>
            <div className="news-container" ref={scrollContainerRef}>
                {articles.length > 0 ? (
                    articles.filter(article => article.image_url).slice(0, 6).map((article, index) => (
                        <div className="col" key={index}>
                            <NewsItem1 
                                title={article.title} 
                                discription={article.description} 
                                image_url={article.image_url} 
                                source_url={article.source_url} 
                            />
                        </div>
                    ))
                ) : (
                    <div className="col-12">
                        <p>No articles available.</p>
                    </div>
                )}
            </div>
            <button className="scroll-button right" onClick={scrollRight}>
                <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path d="M9 6L15 12L9 18" stroke="white" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                </svg>
            </button>
        </div>
    );
}

export default News1;
