import React, { useEffect, useState, useRef } from "react";
import NewsItem1 from "./NewsItem1";
import './News.css';

const News1 = () => {
    const [articles, setArticles] = useState([]);
    const scrollContainerRef = useRef(null);

    useEffect(() => {
        fetch("https://newsdata.io/api/1/news?apikey=pub_66465d644c11d4df42868252ecc7a8089cd1d&q=cricket")
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then((data) => setArticles(data.results || []))
            .catch((error) => console.error("Error fetching articles:", error));
    }, []);

    const scrollLeft = () => {
        if (scrollContainerRef.current) {
            scrollContainerRef.current.scrollBy({ left: -300, behavior: "smooth" });
        }
    };

    const scrollRight = () => {
        if (scrollContainerRef.current) {
            scrollContainerRef.current.scrollBy({ left: 300, behavior: "smooth" });
        }
    };

    return (
        <div className="news-wrapper">
            <button className="scroll-button left" onClick={scrollLeft}>&#10094;</button> {/* Left Arrow */}
            <div className="news-container" ref={scrollContainerRef}>
                <div className="single-row">
                    {articles && articles.length > 0 ? (
                        articles.filter(article => article.image_url).slice(0, 5).map((article, index) => (
                            <div className="col" key={index}>
                                <NewsItem1 
                                    title={article.title} 
                                    discription={article.description} 
                                    image_url={article.image_url} 
                                    source_url={article.source_url} 
                                />
                            </div>
                        ))
                    ) : (
                        <div className="col-12">
                            <p>No articles available.</p>
                        </div>
                    )}
                </div>
            </div>
            <button className="scroll-button right" onClick={scrollRight}>&#10095;</button> {/* Right Arrow */}
        </div>
    );
}

export default News1;
