import React, { useEffect, useState, useRef } from "react";
import VenueCard from "./VenueCard";
import Loader from "./Loader";
import "./Venue.css";

function Venue() {
  const effectRan = useRef(false);
  const [venues, setVenues] = useState([]);
  const [loading, setLoading] = useState(true);
  const [canScrollLeft, setCanScrollLeft] = useState(false);
  const [canScrollRight, setCanScrollRight] = useState(true); // Initially true
  const containerRef = useRef(null);

  useEffect(() => {
    if (!effectRan.current) {
      const fetchVenues = async () => {
        try {
          console.log("Fetching venues...");
          const response = await fetch("http://localhost:5000/shop/loadvenues", {
            credentials: "include",
          });

          if (response.ok) {
            const data = await response.json();
            console.log("Fetched Data:", data);

            if (Array.isArray(data) && data.length > 0) {
              setVenues(data);
            } else {
              console.warn("Fetched data is not in expected format:", data);
            }
          } else {
            console.error("Failed to load venues:", response.statusText);
          }
        } catch (error) {
          console.error("Error fetching venues:", error);
          alert("An error occurred while fetching venues.");
        } finally {
          setLoading(false);
          console.log("Loading finished, venues should appear.");
        }
      };

      fetchVenues();
      effectRan.current = true;
    }

    return () => {
      effectRan.current = true;
    };
  }, []);

  useEffect(() => {
    updateScrollButtons(); // Check scroll positions when venues are loaded
  }, [venues]);

  const updateScrollButtons = () => {
    if (containerRef.current) {
      setCanScrollLeft(containerRef.current.scrollLeft > 0);
      setCanScrollRight(
        containerRef.current.scrollLeft < containerRef.current.scrollWidth - containerRef.current.clientWidth
      );
    }
  };

  // Function to scroll left
  const scrollLeft = () => {
    if (containerRef.current) {
      containerRef.current.scrollBy({ left: -300, behavior: "smooth" });
      setTimeout(updateScrollButtons, 300);
    }
  };

  // Function to scroll right
  const scrollRight = () => {
    if (containerRef.current) {
      containerRef.current.scrollBy({ left: 300, behavior: "smooth" });
      setTimeout(updateScrollButtons, 300);
    }
  };

  // Detect manual scrolling and update buttons
  const handleScroll = () => {
    updateScrollButtons();
  };

  if (loading) {
    return <Loader />;
  }

  return (
    <div className="app">
      <div className="book-venues">
        <div className="heading">
          <h2 className="heading2">Book Venues</h2>
          <a href="/Book" className="see-all">SEE ALL VENUES</a>
        </div>

        {/* Scroll buttons */}
        <div className="scroll-container">
          <button className={`scroll-button left ${canScrollLeft ? "active" : "disabled"}`} onClick={scrollLeft}>
            ❮
          </button>
          <div className="venues-container" ref={containerRef} onScroll={handleScroll}>
            {venues.slice(0, 6).map((venue, index) => (
              <VenueCard key={index} venue={venue} />
            ))}
          </div>
          <button className={`scroll-button right ${canScrollRight ? "active" : "disabled"}`} onClick={scrollRight}>
            ❯
          </button>
        </div>
      </div>
    </div>
  );
}

export default Venue;
