import React, { useEffect, useState, useRef } from 'react';
import VenueCard from './VenueCard';
import Loader from './Loader';
import './Venue.css';

function Venue() {
  const effectRan = useRef(false);
  const [venues, setVenues] = useState([]);
  const [loading, setLoading] = useState(true);
  const scrollContainerRef = useRef(null);

  useEffect(() => {
    if (effectRan.current === false) {
      const venueload = async () => {
        try {
          const response = await fetch('http://localhost:5000/shop/loadvenues', {
            credentials: 'include'
          });
          
          if (response.ok) {
            const data = await response.json();
            if (Array.isArray(data) && data.length > 0) {
              setVenues(data);
            }
          }
        } catch (error) {
          alert('An error occurred while fetching venues.');
        } finally {
          setLoading(false);
        }
      };

      venueload();
      effectRan.current = true;
    }
    
    return () => {
      effectRan.current = true;
    };
  }, []);

  // Function to scroll left
  const scrollLeft = () => {
    if (scrollContainerRef.current) {
      scrollContainerRef.current.scrollBy({ left: -300, behavior: 'smooth' });
    }
  };

  // Function to scroll right
  const scrollRight = () => {
    if (scrollContainerRef.current) {
      scrollContainerRef.current.scrollBy({ left: 300, behavior: 'smooth' });
    }
  };

  if (loading) {
    return <Loader />;
  }

  return (
    <div className="app">
      <div className="book-venues">
        <div className="heading">
          <h2 className="heading2">Book Venues</h2>
          <a href="/Book" className="see-all">SEE ALL VENUES</a>
        </div>
        <div className="venues-wrapper">
          <button className="scroll-button left" onClick={scrollLeft}>◄</button>
          <div className="venues-container" ref={scrollContainerRef}>
            {venues.slice(0, 6).map((venue, index) => (
              <VenueCard key={index} venue={venue} />
            ))}
          </div>
          <button className="scroll-button right" onClick={scrollRight}>►</button>
        </div>
      </div>
    </div>
  );
}

export default Venue;
